import * as React from "react";
import Box from "@mui/material/Box";
import Switch from "@mui/material/Switch";
import Paper from "@mui/material/Paper";
import Fade from "@mui/material/Fade";
import FormControlLabel from "@mui/material/FormControlLabel";
import DataGridDemo from "./DataGrid";
import { useMemo } from "react";
import { width } from "@mui/system";
const icon = (memoizedData) => {
  return (
    <Paper sx={{ m: 1,  width:400 }} elevation={4}>
      <DataGridDemo memoizedData={memoizedData}></DataGridDemo>
      {/* <Box component="svg" sx={{ width: 100, height: 100 }}></Box> */}
    </Paper>
  );
};

export default function SimpleFade(props) {
  const [checked, setChecked] = React.useState(false);
  const { data, isLoading } = props;
  const memoizedData = useMemo(() => (data ? data.data : []), [data]);

  const handleChange = () => {
    setChecked((prev) => !prev);
  };

  return (
    <Box sx={{ height: 180 }}>
      <FormControlLabel
        control={<Switch checked={checked} onChange={handleChange} />}
        label="Show"
      />
      <Box sx={{ display: "flex" }}>
        {/* <DataGridDemo memoizedData={memoizedData}></DataGridDemo> */}
        <Fade in={checked}>{icon(memoizedData)}</Fade>
      </Box>
    </Box>
  );
}
